CREATE TABLE Proxy (id INT AUTO_INCREMENT NOT NULL, ch VARCHAR(255) NOT NULL, prefix VARCHAR(255) NOT NULL, blockedDomains LONGTEXT NOT NULL COMMENT '(DC2Type:array)', baseUrl VARCHAR(255) NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE Contact (id INT AUTO_INCREMENT NOT NULL, name VARCHAR(255) NOT NULL, email VARCHAR(255) NOT NULL, message LONGTEXT NOT NULL, date_add DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
